CREATE PROCEDURE [xls27].[xl_validation_list_vat_rate_id]
    @company_id int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

SELECT
    t.id
    , COALESCE(tr.name, t.name) AS name
FROM
    dbo27.vat_rates t
    LEFT OUTER JOIN dbo27.translations tr ON tr.company_id = t.company_id AND tr.table_id = 4 AND tr.member_id = t.id AND tr.language = @data_language
WHERE
    t.is_active = 1
    AND t.company_id = @company_id
ORDER BY
    t.sort_order
    , t.name

END


